var rotationSnap = 90;

Draggable.create(".boxes", {
    type: "rotation",
    throwProps: !0,
    snap: function(a) {
        return Math.round(a / rotationSnap) * rotationSnap;
    },
    onPress: function(a) {
        $(".box").removeClass("autogiro");
    },
    minDuration: 450
});

var rotationSnap = 90;

Draggable.create("#molino-inicio", {
    type: "rotation",
    throwProps: !0,
    snap: function(a) {
        return Math.round(a / rotationSnap) * rotationSnap;
    },
    onDragEnd: function(a) {
        $("#black, #intro .molino, #intro .right, #textos-inicio, #intro .flecha, nav").removeClass("current");
    },
    onPress: function(a) {
        $("#textos-inicio img").css("opacity", "0");
    },
    minDuration: 450
}), $("nav a").on("click", function(a) {
    if (a.preventDefault(), $(this).hasClass("menu")) $("nav").toggleClass("activo"); else {
        var b = $(this).data("seccion");
        $("#" + b).addClass("activo").siblings().removeClass("activo"), $("body").css("background-image", "url(assets/images/bg_" + b + ".jpg)"), 
        $(this).addClass("activo").siblings().removeClass("activo"), $("nav").toggleClass("activo");
    }
}), $("header a, #error a").on("click", function(a) {
    a.preventDefault(), location.reload();
});